import './global.css';
import emailjs from "emailjs-com";

const showMessageAboutSuccess = () => {
    alert("Your message was successfully sent")
    resetData()
}

export const Mailer = async (name, email, textMessage) => {
    emailjs.init("user_AJGy3zMpNl2Rxs4CFFafD");
    let form = {
        name: name,
        email: email,
        textMessage: textMessage
    }
    let response;
    emailjs.send('service_tauvzqc', 'template_26nh4ab', form)
        .then((result) => {
            showMessageAboutSuccess()
        }, (error) => {
            alert("Error, your message wasn't sent")
        });
}
