import './global.css';
import emailjs from "emailjs-com";

let STATUS_ok = 1
let STATUS_error = 2

export const Mailer = async (name, email, textMessage) => {
    emailjs.init("user_AJGy3zMpNl2Rxs4CFFafD");
    let form = {
        name: name,
        email: email,
        textMessage: textMessage
    }
    let response;
    emailjs.send('service_tauvzqc', 'template_26nh4ab', form)
        .then((result) => {
            response = STATUS_ok
        }, (error) => {
            response = STATUS_error
        });
    return response
}
